class Message extends React.Component
{
    constructor(){
        super();
        this.state={
            message:"welcome visitor"
        }
    }

    changeMessage(){
        this.setState({
            message:"thank u for subscribing"
        })
    }
    render()
    {
        return(
            <div>
                <h1>{this.state.message}</h1>
                <button onClick={()=>this.changeMessage()}>Subscribe</button>
            </div>
        )
    }
}
export default Message
